import React, { useEffect, useReducer, useState } from "react";
import update from "immutability-helper";
import axios from "axios";

function reducer(state, action) {
      switch (action.type) {
        case "update":
          axios.put(`http://localHost:9999/api/v1/ideas/${action.fromId}`, 
            action.data,          
            {headers: {"Content-Type": "application/json"}}
          ).then(res => console.log("successful update"))
          return update(state, {
            [action.id]: {
              $merge: action.data,
            },
          });
        case "create":
          axios.post("http://localHost:9999/api/v1/ideas", 
                    Object.assign({
                      id:action.id,linkList: []}, action.data)
          ).then(res => console.log("successful addition"))
          return update(state, {
            [action.id]: {
              $set: Object.assign(
                { id: action.id, linkList: new Set() },
                action.data
              ),
            },
          });
        case "link":
          axios.put(`http://localHost:9999/api/v1/ideas/${action.fromId}`, 
            {link:action.toId},          
            {headers: {"Content-Type": "application/json"}}
          ).then(res => console.log("successful link"))
          return update(state, {
            [action.fromId]: {
              linkList: { $add: [action.toId] },
            },
          });
        case "delete":
          axios.delete(`http://localHost:9999/api/v1/ideas/${action.deleteId}`)
          .then(res => console.log("successful delete"))
          return update(state, 
            {$unset: [action.deleteId]}
          )
        case "initialIdeas":
            return update(state, {
              $set:action.data
            });
        default:
          throw new Error(`Unexpected action type: ${action.type}`);
      }
    }

export function useIdeaManager() {
  const [selectedId, setSelectedId] = useState("a");

  const [ideas, ideasDispatch] = useReducer(reducer, {});
  
  useEffect(() => {
    const ideasImport = {};
    axios.get("http://localHost:9999/api/v1/ideas").then(
      res =>{
        res.data.forEach(element => {
        element.linkList = new Set(element.linkList)
        ideasImport[element.id] = element;
      });
      
        ideasDispatch({
          type: "initialIdeas",
          data: ideasImport
        });
      }
    )
    
  }, [])

  return {
    ideas: ideas,
    ideasDispatch: ideasDispatch,
    selectedId: selectedId,
    setSelectedId: setSelectedId,
  };
}

export const IdeaContext = React.createContext();
