import React, { useEffect, useReducer, useState } from "react";
import update from "immutability-helper";
import axios from "axios";

export function useIdeaManager() {
  const [selectedId, setSelectedId] = useState("a");

  

  const [ideas, ideasDispatch] = useReducer(
    (state, action) => {
      switch (action.type) {
        case "update":
          return update(state, {
            [action.id]: {
              $merge: action.data,
            },
          });
        case "create":
          return update(state, {
            [action.id]: {
              $set: Object.assign(
                { id: action.id, linkList: new Set() },
                action.data
              ),
            },
          });
        case "link":
          return update(state, {
            [action.fromId]: {
              linkList: { $add: [action.toId] },
            },
          });
        case "initialize":
          return update(state, {
            [action.id]: {
              $set: Object.assign(
                { id: action.id, linkList: action.links },
                action.data
              ),
            },
          });
        default:
          throw new Error(`Unexpected action type: ${action.type}`);
      }
    },{}
    // {
    //   a: {
    //     id: "a",
    //     top: 20,
    //     left: 80,
    //     content: "Here is an Example to get you Started",
    //     linkList: new Set("b"),
    //   },
    //   b: {
    //     id: "b",
    //     top: 180,
    //     left: 20,
    //     content: "Great Idea!",
    //     linkList: new Set(),
    //   },
    // },
    
          // res.data.reduce(
          //   (obj, item) => ({
          //     ...obj, [item["id"]]: item
          //   }), {}
          // )
  )
  
  useEffect(() => {
    axios.get("http://localHost:9999/api/v1/ideas").then(
      res =>{res.data.forEach(element => {
        ideasDispatch({
          type: "initialize",
          id: element.id,
          data: element,
        });
    console.log(ideas)
      });
      }
    )
  }, [])

  return {
    ideas: ideas,
    ideasDispatch: ideasDispatch,
    selectedId: selectedId,
    setSelectedId: setSelectedId,
  };
}

export const IdeaContext = React.createContext();
